import os
import unittest

try:
    import test_fw_const_TheisProblem
except ImportError:
    raise ImportError(
        'No module named test_fw_const_TheisProblem. \n\n' + 'Solution: ' +
        ' Add the path /path-to-SHEMAT-Suite_Models/' +
        'SHEMAT-Suite_Models/deltatests/site-packages to the' + ' PYTHONPATH.')

def full_suite():
    full_suite = unittest.TestSuite()

    full_suite.addTest(test_fw_const_TheisProblem.local_suite())

    return full_suite


if __name__ == "__main__":
    runner = unittest.TextTestRunner(verbosity=2)
    runner.run(full_suite())
