import unittest
import numpy as np
import numpy.testing as npt
import h5py

try:
    import SHEMATtest_dirs as std
except ImportError:
    raise ImportError('No module named SHEMATtest_dirs. \n\n' + 'Solution: ' +
                      ' Add the path /path-to-SHEMAT-Suite_Models/' +
                      'SHEMAT-Suite_Models/deltatests/site-packages to the' +
                      ' PYTHONPATH.')


class Tests_fw_bas_2Dfancy_ForcedConvection(unittest.TestCase):
    def test_comp_equal(self):

        fhdf1 = h5py.File(std.shemat_suite_models_dir +
                          '/fw_bas_2Dfancy_ForcedConvection' +
                          std.result_dir + '/forced_convection_final.h5')
        comp_array_desired = np.array(fhdf1['comp'])
        fhdf1.close()

        fhdf2 = h5py.File(
            std.shemat_suite_models_dir +
            '/fw_bas_2Dfancy_ForcedConvection/forced_convection_final.h5')
        comp_array_actual = np.array(fhdf2['comp'])
        fhdf2.close()

        npt.assert_allclose(comp_array_actual, comp_array_desired, rtol=1e-8)

