def local_suite():
    local_suite = unittest.makeSuite(
        Tests_fw_bas_2Dfancy_ForcedConvection, prefix='test')
    return local_suite


def local_run():
    runner = unittest.TextTestRunner(verbosity=2)
    runner.run(local_suite())


if __name__ == "__main__":
    local_run()
